#define _CRT_SECURE_NO_WARNINGS 1
#include <iostream>
using namespace std;

bool canPermutePalindrome(string s) {
    int arr[128] = { 0 };
    for (int i = 0; i < s.size(); ++i) {
        ++arr[s[i] - 0];
    }
    int count = 0;
    for (int i = 0; i < 128; ++i) {
        if (arr[i] == 1 || (arr[i] > 1 && arr[i] % 2 != 0))
            ++count;
    }

    if (count > 1)
        return false;
    return true;
}

int main()
{
    string s("");
    cout << canPermutePalindrome(s) << endl;
	return 0;
}